// Reconciliation Logic
const clinicalNote = $input.item(0).json;
const checkoutSheet = $input.item(1).json;
const patientData = $node["Fetch Patient Data"].json;
const appointmentData = $node["Fetch Appointment Data"].json;

const conflicts = [];
const reconciled = {
  patient: {
    name: `${patientData.first_name} ${patientData.last_name}`,
    dob: patientData.date_of_birth,
    id: patientData.id,
    mrn: patientData.chart_id
  },
  appointment: {
    id: appointmentData.id,
    date: appointmentData.scheduled_time,
    provider: appointmentData.doctor,
    location: appointmentData.office
  },
  diagnoses: [],
  procedures: [],
  medications: {
    new: [],
    continued: [],
    discontinued: []
  },
  restrictions: [],
  followUp: [],
  orders: {
    imaging: [],
    labs: [],
    referrals: []
  },
  homeInstructions: [],
  redFlags: []
};

// Reconcile Diagnoses
const clinicalDx = clinicalNote.diagnosis_codes || [];
const checkoutDx = checkoutSheet.diagnoses || [];

if (JSON.stringify(clinicalDx.sort()) !== JSON.stringify(checkoutDx.sort())) {
  conflicts.push({
    field: 'diagnoses',
    clinical: clinicalDx,
    checkout: checkoutDx,
    resolution: 'Using Clinical Note as authoritative source'
  });
}
reconciled.diagnoses = clinicalDx.length > 0 ? clinicalDx : checkoutDx;

// Reconcile Procedures
const clinicalProc = clinicalNote.procedures || [];
const checkoutProc = checkoutSheet.procedures_performed || [];

if (JSON.stringify(clinicalProc.sort()) !== JSON.stringify(checkoutProc.sort())) {
  conflicts.push({
    field: 'procedures',
    clinical: clinicalProc,
    checkout: checkoutProc,
    resolution: 'Merging both sources'
  });
}
reconciled.procedures = [...new Set([...clinicalProc, ...checkoutProc])];

// Reconcile Medications
reconciled.medications.new = clinicalNote.new_medications || [];
reconciled.medications.continued = clinicalNote.continued_medications || [];
reconciled.medications.discontinued = clinicalNote.discontinued_medications || [];

// Reconcile Restrictions
const clinicalRestrict = clinicalNote.restrictions || [];
const checkoutRestrict = checkoutSheet.restrictions || [];

if (JSON.stringify(clinicalRestrict) !== JSON.stringify(checkoutRestrict)) {
  conflicts.push({
    field: 'restrictions',
    clinical: clinicalRestrict,
    checkout: checkoutRestrict,
    resolution: 'Merging both sources'
  });
}
reconciled.restrictions = [...new Set([...clinicalRestrict, ...checkoutRestrict])];

// Follow-up Instructions
reconciled.followUp = checkoutSheet.follow_up_appointments || clinicalNote.follow_up_instructions || [];

// Orders
reconciled.orders.imaging = checkoutSheet.imaging_orders || clinicalNote.imaging_orders || [];
reconciled.orders.labs = checkoutSheet.lab_orders || clinicalNote.lab_orders || [];
reconciled.orders.referrals = checkoutSheet.referrals || [];

// Home Care Instructions
reconciled.homeInstructions = clinicalNote.home_care_instructions || checkoutSheet.patient_education || [];

// Red Flags (Emergency Instructions)
reconciled.redFlags = clinicalNote.red_flag_instructions || [
  "Return to ER if symptoms worsen",
  "Call 911 for chest pain, difficulty breathing, or severe bleeding"
];

// Calculate conflict percentage
const totalFields = 6;
const conflictPercentage = ((totalFields - conflicts.length) / totalFields * 100).toFixed(2);

return {
  json: {
    reconciled,
    conflicts,
    metadata: {
      timestamp: new Date().toISOString(),
      accuracy: `${conflictPercentage}%`,
      totalConflicts: conflicts.length,
      status: conflicts.length === 0 ? 'CLEAN' : 'CONFLICTS_FOUND'
    }
  }
};

// Audit Log Entry
const data = $input.first().json;

const auditLog = {
  timestamp: new Date().toISOString(),
  appointment_id: data.reconciled.appointment.id,
  patient_id: data.reconciled.patient.id,
  patient_name: data.reconciled.patient.name,
  generated_by: 'Automated System',
  conflicts_detected: data.conflicts.length,
  accuracy_percentage: data.metadata.accuracy,
  status: data.metadata.status,
  conflicts: data.conflicts,
  document_location: 'DrChrono + Cloud Storage',
  processing_time: '< 60 seconds',
  version: '1.0'
};

return { json: auditLog };

